import { Component, OnInit } from '@angular/core';

interface IPlano {
  infos: IInfos;
}

interface IInfos {
  tipo: string;
  preco: number;
}

@Component({
  selector: 'app-card-roxo',
  templateUrl: './card-roxo.component.html',
  styleUrls: ['./card-roxo.component.scss']
})
export class CardRoxoComponent implements OnInit {
 
  //@ts-ignore
  plano: IPlano = {
    infos: {
      tipo: 'Simples',
      preco: 200,
    },
  };

  constructor() { }

  ngOnInit(): void {
  }

}
<div class="card">
    <div class="card__plan card__item">Plano <b>{{ plano.infos.tipo }}</b></div>
    <div class="card__price cart__item">R$ {{ plano.infos.preco }},00/Mês</div>
    <app-card-roxo-button></app-card-roxo-button>
    <app-card-button-cancel id="primeiro"></app-card-button-cancel>

    <mat-slider min="1" max="5" step="0.5" value="1.5" class="custom-slider">
        <input matSliderThumb>
      </mat-slider>
</div>
